INSERT INTO `ims_fy_lesson_parent` VALUES (3946, 2, 1726, 0, 3, '{\"study_name\":\"\",\"buynow_name\":\"\"}', NULL, NULL, '大湾区激光企业峰会及2020《红光奖》颁奖盛典直播', 0.00, 0, 0, 0, 0.0, 0, 'images/2/2020/06/R77Nq1Bz1dgpPel00gdLNHVEC7c1b3.jpg', NULL, '&lt;p&gt;&lt;strong&gt;第一场：大湾区激光企业家峰会暨协会第二届第三次会员大会 (14:00 - 18:00)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;strong style=&quot;white-space: normal;&quot;&gt;第二场&lt;strong style=&quot;white-space: normal;&quot;&gt;：&lt;/strong&gt;&lt;/strong&gt;2020《红光奖》颁奖盛典&lt;strong style=&quot;white-space: normal;&quot;&gt;&nbsp;(18:30 - 21:00)&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;2020年，突发全球性新冠疫情，全球经济发展受到严重影响。我国激光行业也遇到严峻考验。为了协助企业走出困境，广东省激光行业协会及XZQ连续不断走访、调研了珠江三角洲及湖北省的激光行业企业，了解企业难处，积极协助行业企业疏通与政府及下游运用企业的沟通与合作。在此背景下，特召集全国激光行业上、中、下游产业链的领袖菁英们相聚一起，以两会“新基建”为轴心，旨在探讨激光行业在后疫情之下行业未来发展脉络，助推产业升级，共同推动我国激光产业发展。&lt;/p&gt;', '', 0, 0, 0, 10715, 'ico-hot', 0.00, 564, 'a:4:{s:15:\"commission_type\";i:0;s:11:\"commission1\";d:0;s:11:\"commission2\";d:0;s:11:\"commission3\";d:0;}', 0, 0, 1, '382', 'null', 100, 0, NULL, 0, '{\"title\":\"\\u5927\\u6e7e\\u533a\\u6fc0\\u5149\\u4f01\\u4e1a\\u5cf0\\u4f1a\\u53ca2020\\u300a\\u7ea2\\u5149\\u5956\\u300b\\u9881\\u5956\\u76db\\u5178\\u76f4\\u64ad\",\"images\":\"images\\/2\\/2020\\/06\\/R77Nq1Bz1dgpPel00gdLNHVEC7c1b3.jpg\",\"descript\":\"\\u4f1a\\u8bae\\u53ca\\u9881\\u5956\\u65f6\\u95f4:2020\\u5e746\\u670820\\u65e5\\uff08\\u661f\\u671f\\u516d\\uff0914:00-21:00\"}', 1, NULL, 1588563558, 1588563558, '', '[]', NULL, 0, 0, 0, 0.00, 0, 0, 1, 0, 0, 0, '{\"type\":\"2\",\"videourl\":\"\",\"stream_name\":\"ut107w_2_3946\",\"sid\":\"22039119\",\"ssid\":\"22039119\",\"tid\":\"16777217\",\"starttime\":\"2020-06-20 18:55:00\",\"endtime\":\"2020-06-20 21:30:00\",\"chatroom\":\"1\",\"virtual_num\":2639}');
