INSERT INTO `ims_fy_lesson_parent` VALUES (3999, 2, 1726, 0, 0, '{\"study_name\":\"\",\"study_link\":\"\",\"buynow_name\":\"\",\"buynow_link\":\"\"}', 'null', 'null', '大湾区激光企业峰会及2020《红光奖》颁奖盛典回顾', 0.00, 0, 0, 0, 0.0, 0, 'images/2/2020/06/H4R9r6v4A999Ew46jp6W963jR148nw.jpg', '', '&lt;p&gt;2020年，突发全球性新冠疫情，全球经济发展受到严重影响。我国激光行业也遇到严峻考验。为了协助企业走出困境，广东省激光行业协会及XZQ连续不断走访、调研了珠江三角洲及湖北省的激光行业企业，了解企业难处，积极协助行业企业疏通与政府及下游运用企业的沟通与合作。在此背景下，特召集全国激光行业上、中、下游产业链的领袖菁英们相聚一起，以两会“新基建”为轴心，旨在探讨激光行业在后疫情之下行业未来发展脉络，助推产业升级，共同推动我国激光产业发展。&lt;/p&gt;', '', 0, 0, 0, 2478, '', 0.00, 564, 'a:4:{s:15:\"commission_type\";i:0;s:11:\"commission1\";d:0;s:11:\"commission2\";d:0;s:11:\"commission3\";d:0;}', 0, 1, 1, '384', 'null', 100, 0, '', 0, '{\"title\":\"\",\"images\":\"\",\"descript\":\"\"}', 1, '', 1592905759, 1592963022, '', '[]', 'null', 0, 0, 0, 0.00, 0, 0, 0, 2, 0, 0, '');
