  `poster_config` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '课程海报配置',
  `addtime` int(10) NOT NULL COMMENT '添加时间',
  `update_time` int(11) NULL DEFAULT NULL COMMENT '章节最后更新时间',
  `poster_bg` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '海报背景图',
  `poster_setting` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '课程海报参数',
  `service` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '加群客服',
  `recommend_free_num` int(4) NOT NULL DEFAULT 0 COMMENT '免费学习直接推荐人数',
  `recommend_free_day` int(4) NOT NULL DEFAULT 0 COMMENT '免费学习期限',
