  INDEX `idx_addtime`(`addtime`) USING BTREE,
  INDEX `attribute1`(`attribute1`) USING BTREE,
  INDEX `pid`(`pid`) USING BTREE,
  INDEX `attribute2`(`attribute2`) USING BTREE,
  INDEX `lesson_type`(`lesson_type`) USING BTREE,
  INDEX `vipview`(`vipview`) USING BTREE
